  const raw = [
    body.inbox?.channel_type,
    body.conversation?.channel,
    body.conversation?.meta?.channel,
    body.inbox?.name
  ]
    .filter(Boolean)
    .join(' ')
    .toString()
    .toLowerCase();

  const phone = (body.sender?.phone_number || body.conversation?.meta?.sender?.phone_number || '').toString().trim();
  const identifier = (body.sender?.identifier || body.conversation?.meta?.sender?.identifier || body.conversation?.contact_inbox?.source_id || '').toString().trim();

  if (raw.includes('instagram') || raw.includes('facebook') || (identifier && !phone)) return 'INSTAGRAM';
  if (raw.includes('whatsapp') || raw.includes('wpp') || raw.includes('twilio')) return 'WHATSAPP';
  if (phone) return 'WHATSAPP';
  if (identifier) return 'INSTAGRAM';
  return 'WHATSAPP';
})() }}

