={{ (() => {
  const body = $('Webhook').item.json.body || {};
  const raw = [
    body.inbox?.channel_type,
    body.conversation?.channel,
    body.conversation?.meta?.channel,
    body.inbox?.name
  ]
    .filter(Boolean)
    .join(' ')
    .toString()
    .toLowerCase();

  const phone = (body.sender?.phone_number || body.conversation?.meta?.sender?.phone_number || '').toString().trim();
  const igsid = (body.sender?.identifier || body.conversation?.meta?.sender?.identifier || body.conversation?.contact_inbox?.source_id || '').toString().trim();
  const conversationId = body.conversation?.id || body.id || 'unknown';

  if (raw.includes('instagram') || raw.includes('facebook') || (igsid && !phone)) {
    return igsid ? 'instagram:' + igsid : 'instagram:conversation:' + conversationId;
  }

  if (raw.includes('whatsapp') || raw.includes('wpp') || raw.includes('twilio')) {
    return phone ? 'whatsapp:' + phone : 'whatsapp:conversation:' + conversationId;
  }

  if (phone) return 'whatsapp:' + phone;
  if (igsid) return 'instagram:' + igsid;
  return 'chatwoot:conversation:' + conversationId;
})() }}

